mapper.select:
SELECT order_id, order_no, user_id, total_amount, status, shipping_address, payment_time, create_time, update_time FROM orders
AND user_id = #{userId}
AND order_id IN ( SELECT DISTINCT o.order_id FROM orders o LEFT JOIN order_item oi ON o.order_id = oi.order_id LEFT JOIN product_sku ps ON oi.sku_id = ps.sku_id LEFT JOIN product p ON ps.product_id = p.product_id WHERE p.shop_id = #{shopId} )
AND status = #{orderStatus}
AND order_no LIKE CONCAT('%', #{orderNo}, '%')
AND create_time >= #{startDate}
AND create_time <= #{endDate}
ORDER BY create_time DESC LIMIT #{offset}, #{pageSize}
